package com.java;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 2;
		System.out.println("Before left shift operation: " + x);
		int y = x >> 1;
		System.out.println("After left shift: " + y);

		int z = x << 1;
		System.out.println("After right shift: " + z);
	}

}
